import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaTrash, FaEdit, FaSave, FaTimes } from 'react-icons/fa';

const MemoBoard = () => {
  const [memos, setMemos] = useState([]);
  const [newMemo, setNewMemo] = useState('');
  const [editingMemo, setEditingMemo] = useState(null);
  const [editText, setEditText] = useState('');

  useEffect(() => {
    fetchMemos();
  }, []);

  const fetchMemos = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:5001/api/memos', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMemos(response.data);
    } catch (error) {
      console.error('Error fetching memos:', error);
    }
  };

  const addMemo = async (e) => {
    e.preventDefault();
    if (!newMemo.trim()) return;

    try {
      const token = localStorage.getItem('token');
      const response = await axios.post('http://localhost:5001/api/memos', {
        content: newMemo.trim()
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMemos([...memos, response.data]);
      setNewMemo('');
    } catch (error) {
      console.error('Error adding memo:', error);
    }
  };

  const deleteMemo = async (id) => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`http://localhost:5001/api/memos/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMemos(memos.filter(memo => memo._id !== id));
    } catch (error) {
      console.error('Error deleting memo:', error);
    }
  };

  const startEditing = (memo) => {
    setEditingMemo(memo._id);
    setEditText(memo.content);
  };

  const saveEdit = async (id) => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.put(`http://localhost:5001/api/memos/${id}`, {
        content: editText.trim()
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMemos(memos.map(memo => 
        memo._id === id ? response.data : memo
      ));
      setEditingMemo(null);
    } catch (error) {
      console.error('Error updating memo:', error);
    }
  };

  return (
    <div className="p-4">
      <form onSubmit={addMemo} className="mb-6">
        <div className="flex gap-2">
          <input
            type="text"
            value={newMemo}
            onChange={(e) => setNewMemo(e.target.value)}
            placeholder="Add a new memo..."
            className="flex-1 p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <button
            type="submit"
            className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-colors"
          >
            Add Memo
          </button>
        </div>
      </form>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {memos.map((memo) => (
          <div
            key={memo._id}
            className="bg-yellow-100 p-4 rounded-lg shadow-md relative group"
          >
            {editingMemo === memo._id ? (
              <div className="space-y-2">
                <textarea
                  value={editText}
                  onChange={(e) => setEditText(e.target.value)}
                  className="w-full p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                  rows="3"
                />
                <div className="flex justify-end gap-2">
                  <button
                    onClick={() => saveEdit(memo._id)}
                    className="p-2 text-green-600 hover:text-green-700"
                  >
                    <FaSave />
                  </button>
                  <button
                    onClick={() => setEditingMemo(null)}
                    className="p-2 text-gray-600 hover:text-gray-700"
                  >
                    <FaTimes />
                  </button>
                </div>
              </div>
            ) : (
              <>
                <p className="whitespace-pre-wrap">{memo.content}</p>
                <div className="absolute top-2 right-2 opacity-0 group-hover:opacity-100 transition-opacity">
                  <button
                    onClick={() => startEditing(memo)}
                    className="p-1 text-blue-600 hover:text-blue-700 mr-2"
                  >
                    <FaEdit />
                  </button>
                  <button
                    onClick={() => deleteMemo(memo._id)}
                    className="p-1 text-red-600 hover:text-red-700"
                  >
                    <FaTrash />
                  </button>
                </div>
              </>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default MemoBoard;
