import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { Rnd } from 'react-rnd';
import { FaTrash, FaEdit, FaStickyNote, FaTimes, FaUser, FaBold, FaUnderline, FaFont } from 'react-icons/fa';

const DEFAULT_WIDTH = 200;
const DEFAULT_HEIGHT = 200;
const COLORS = [
  '#ffd700', // Yellow
  '#98fb98', // Pale Green
  '#87ceeb', // Sky Blue
  '#dda0dd', // Plum
  '#f0e68c', // Khaki
  '#e6e6fa', // Lavender
  '#ffb6c1', // Light Pink
  '#b0e0e6', // Powder Blue
];

const FONT_SIZES = [
  { label: 'Small', value: '0.875rem' },
  { label: 'Normal', value: '1rem' },
  { label: 'Large', value: '1.25rem' },
  { label: 'X-Large', value: '1.5rem' }
];

const MemoBoard = () => {
  const [memos, setMemos] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [error, setError] = useState('');
  const [deleteConfirm, setDeleteConfirm] = useState({ show: false, memoId: null });
  const [user, setUser] = useState(null);
  const [formatting, setFormatting] = useState({});
  const boardRef = useRef(null);

  useEffect(() => {
    fetchMemos();
    fetchUserProfile();
  }, []);

  const fetchUserProfile = async () => {
    try {
      const token = localStorage.getItem('token');
      console.log('Fetching user profile with token:', token);
      const response = await axios.get('http://localhost:5001/api/auth/profile', {
        headers: { Authorization: `Bearer ${token}` }
      });
      console.log('User profile response:', response.data);
      setUser(response.data);
    } catch (error) {
      console.error('Error fetching user profile:', error);
    }
  };

  const fetchMemos = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:5001/api/memos', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMemos(response.data);
      setError('');
    } catch (error) {
      console.error('Error fetching memos:', error);
      setError('Failed to load memos. Please try again.');
    }
  };

  const getRandomColor = () => COLORS[Math.floor(Math.random() * COLORS.length)];

  const addNewMemo = async () => {
    try {
      const token = localStorage.getItem('token');
      const newMemo = {
        content: '',
        x: 50,
        y: 50,
        width: DEFAULT_WIDTH,
        height: DEFAULT_HEIGHT,
        color: getRandomColor(),
        formatting: {
          fontSize: '1rem',
          isBold: false,
          isUnderlined: false
        }
      };

      const response = await axios.post('http://localhost:5001/api/memos', newMemo, {
        headers: { Authorization: `Bearer ${token}` }
      });

      setMemos([...memos, response.data]);
      setEditingId(response.data._id);
      setFormatting(response.data.formatting || {});
      setError('');
    } catch (error) {
      console.error('Error adding memo:', error);
      setError('Failed to add memo. Please try again.');
    }
  };

  const showDeleteConfirm = (memoId) => {
    setDeleteConfirm({ show: true, memoId });
  };

  const hideDeleteConfirm = () => {
    setDeleteConfirm({ show: false, memoId: null });
  };

  const deleteMemo = async (id) => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`http://localhost:5001/api/memos/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMemos(memos.filter(memo => memo._id !== id));
      setError('');
      hideDeleteConfirm();
    } catch (error) {
      console.error('Error deleting memo:', error);
      setError('Failed to delete memo. Please try again.');
    }
  };

  const updateMemo = async (id, updates) => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.put(`http://localhost:5001/api/memos/${id}`, updates, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMemos(memos.map(memo => memo._id === id ? response.data : memo));
      setError('');
    } catch (error) {
      console.error('Error updating memo:', error);
      setError('Failed to update memo. Please try again.');
    }
  };

  const toggleFormat = async (format) => {
    const currentMemo = memos.find(memo => memo._id === editingId);
    if (!currentMemo) return;

    const newFormatting = {
      ...(currentMemo.formatting || {}),
      [format]: !(currentMemo.formatting?.[format] || false)
    };

    try {
      const token = localStorage.getItem('token');
      const response = await axios.put(
        `http://localhost:5001/api/memos/${editingId}`,
        { formatting: newFormatting },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setMemos(memos.map(memo => 
        memo._id === editingId ? { ...memo, formatting: newFormatting } : memo
      ));
      setFormatting(newFormatting);
    } catch (error) {
      console.error('Error updating formatting:', error);
      setError('Failed to update formatting. Please try again.');
    }
  };

  const changeFontSize = async (size) => {
    const currentMemo = memos.find(memo => memo._id === editingId);
    if (!currentMemo) return;

    const newFormatting = {
      ...(currentMemo.formatting || {}),
      fontSize: size
    };

    try {
      const token = localStorage.getItem('token');
      const response = await axios.put(
        `http://localhost:5001/api/memos/${editingId}`,
        { formatting: newFormatting },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setMemos(memos.map(memo => 
        memo._id === editingId ? { ...memo, formatting: newFormatting } : memo
      ));
      setFormatting(newFormatting);
    } catch (error) {
      console.error('Error updating font size:', error);
      setError('Failed to update font size. Please try again.');
    }
  };

  const handleDragStop = (id, e, d) => {
    updateMemo(id, { x: d.x, y: d.y });
  };

  const handleResizeStop = (id, e, direction, ref, delta, position) => {
    updateMemo(id, {
      width: ref.style.width,
      height: ref.style.height,
      x: position.x,
      y: position.y
    });
  };

  return (
    <div className="relative w-full h-full bg-gray-100 rounded-lg overflow-hidden">
      {error && (
        <div className="absolute top-4 left-1/2 transform -translate-x-1/2 bg-red-100 border border-red-400 text-red-700 px-4 py-2 rounded z-50">
          {error}
        </div>
      )}
      
      {/* User Profile and Add Note Button Container */}
      <div className="absolute top-4 right-4 flex items-center gap-4 z-10">
        {/* User Profile */}
        {user && (
          <div className="flex items-center gap-2 bg-white px-3 py-2 rounded-lg shadow-md">
            {console.log('Rendering user profile:', user)}
            {user.profileImage ? (
              <img 
                src={`http://localhost:5001/uploads/${user.profileImage.split('/').pop()}`} 
                alt={user.username} 
                className="w-8 h-8 rounded-full object-cover"
                onError={(e) => {
                  console.error('Error loading profile image:', e);
                  e.target.style.display = 'none';
                }}
              />
            ) : (
              <div className="w-8 h-8 rounded-full bg-gray-200 flex items-center justify-center">
                <FaUser className="text-gray-500" />
              </div>
            )}
            <span className="text-gray-700 font-medium">{user.username}</span>
          </div>
        )}
        
        {/* Add Note Button */}
        <button
          onClick={addNewMemo}
          className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded-lg flex items-center gap-2 shadow-lg transition-all duration-200"
        >
          <FaStickyNote className="text-lg" />
          <span>Add Note</span>
        </button>
      </div>

      {/* Delete Confirmation Dialog */}
      {deleteConfirm.show && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 max-w-sm w-full mx-4 shadow-xl">
            <div className="flex items-center justify-between mb-4">
              <h3 className="text-lg font-semibold text-gray-900">Delete Memo</h3>
              <button
                onClick={hideDeleteConfirm}
                className="text-gray-400 hover:text-gray-500"
              >
                <FaTimes />
              </button>
            </div>
            <p className="text-gray-600 mb-6">
              Are you sure you want to delete this memo? This action cannot be undone.
            </p>
            <div className="flex justify-end gap-3">
              <button
                onClick={hideDeleteConfirm}
                className="px-4 py-2 text-gray-600 hover:text-gray-800 transition-colors"
              >
                Cancel
              </button>
              <button
                onClick={() => deleteMemo(deleteConfirm.memoId)}
                className="px-4 py-2 bg-red-500 text-white rounded hover:bg-red-600 transition-colors"
              >
                Delete
              </button>
            </div>
          </div>
        </div>
      )}
      
      {/* Memo Board */}
      <div 
        ref={boardRef}
        className="w-full h-full p-4"
      >
        {memos.map((memo) => (
          <Rnd
            key={memo._id}
            default={{
              x: memo.x,
              y: memo.y,
              width: memo.width,
              height: memo.height
            }}
            onDragStop={(e, d) => handleDragStop(memo._id, e, d)}
            onResizeStop={(e, direction, ref, delta, position) => 
              handleResizeStop(memo._id, e, direction, ref, delta, position)
            }
            bounds="parent"
            className="shadow-lg"
          >
            <div 
              className="w-full h-full p-4 rounded-lg relative flex flex-col"
              style={{ 
                backgroundColor: memo.color || COLORS[0]
              }}
            >
              {/* Top Controls */}
              <div className="absolute top-2 right-2 flex gap-2">
                <button
                  onClick={() => {
                    setEditingId(memo._id);
                    setFormatting(memo.formatting || {});
                  }}
                  className="text-gray-600 hover:text-blue-500 transition-colors p-1 rounded-full hover:bg-white/20"
                  title="Edit memo"
                >
                  <FaEdit />
                </button>
                <button
                  onClick={() => showDeleteConfirm(memo._id)}
                  className="text-gray-600 hover:text-red-500 transition-colors p-1 rounded-full hover:bg-white/20"
                  title="Delete memo"
                >
                  <FaTrash />
                </button>
              </div>

              {/* Formatting Toolbar */}
              {editingId === memo._id && (
                <div className="absolute top-10 right-2 bg-white rounded-lg shadow-lg p-2 flex gap-2 z-20">
                  <select
                    value={formatting.fontSize || '1rem'}
                    onChange={(e) => changeFontSize(e.target.value)}
                    className="px-2 py-1 rounded border border-gray-300 text-sm"
                  >
                    {FONT_SIZES.map(size => (
                      <option key={size.value} value={size.value}>
                        {size.label}
                      </option>
                    ))}
                  </select>
                  <button
                    onClick={() => toggleFormat('isBold')}
                    className={`p-1 rounded ${formatting.isBold ? 'bg-blue-100 text-blue-600' : 'text-gray-600 hover:bg-gray-100'}`}
                    title="Bold"
                  >
                    <FaBold />
                  </button>
                  <button
                    onClick={() => toggleFormat('isUnderlined')}
                    className={`p-1 rounded ${formatting.isUnderlined ? 'bg-blue-100 text-blue-600' : 'text-gray-600 hover:bg-gray-100'}`}
                    title="Underline"
                  >
                    <FaUnderline />
                  </button>
                </div>
              )}

              {/* Content Area */}
              <div className="flex-1">
                {editingId === memo._id ? (
                  <textarea
                    value={memo.content}
                    onChange={(e) => updateMemo(memo._id, { content: e.target.value })}
                    onBlur={() => setEditingId(null)}
                    className="w-full h-full bg-transparent border-none outline-none resize-none"
                    style={{
                      fontSize: formatting.fontSize || '1rem',
                      fontWeight: formatting.isBold ? 'bold' : 'normal',
                      textDecoration: formatting.isUnderlined ? 'underline' : 'none'
                    }}
                    autoFocus
                  />
                ) : (
                  <div 
                    className="w-full h-full"
                    style={{
                      fontSize: memo.formatting?.fontSize || '1rem',
                      fontWeight: memo.formatting?.isBold ? 'bold' : 'normal',
                      textDecoration: memo.formatting?.isUnderlined ? 'underline' : 'none'
                    }}
                  >
                    {memo.content || 'Click edit to add content...'}
                  </div>
                )}
              </div>
            </div>
          </Rnd>
        ))}
      </div>
    </div>
  );
};

export default MemoBoard;
