import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { Rnd } from 'react-rnd';
import { FaTrash, FaEdit, FaSave, FaTimes, FaPlus, FaStickyNote } from 'react-icons/fa';

const DEFAULT_WIDTH = 200;
const DEFAULT_HEIGHT = 200;
const COLORS = [
  '#ffd700', // Yellow
  '#98fb98', // Pale Green
  '#87ceeb', // Sky Blue
  '#dda0dd', // Plum
  '#f0e68c', // Khaki
  '#e6e6fa', // Lavender
  '#ffb6c1', // Light Pink
  '#b0e0e6', // Powder Blue
];

const MemoBoard = () => {
  const [memos, setMemos] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [error, setError] = useState('');
  const boardRef = useRef(null);

  useEffect(() => {
    fetchMemos();
  }, []);

  const fetchMemos = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:5001/api/memos', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMemos(response.data);
      setError('');
    } catch (error) {
      console.error('Error fetching memos:', error);
      setError('Failed to load memos. Please try again.');
    }
  };

  const getRandomColor = () => COLORS[Math.floor(Math.random() * COLORS.length)];

  const addNewMemo = async () => {
    try {
      const token = localStorage.getItem('token');
      const newMemo = {
        content: '',
        x: 50,
        y: 50,
        width: DEFAULT_WIDTH,
        height: DEFAULT_HEIGHT,
        color: getRandomColor() // Generate color only when creating new memo
      };

      const response = await axios.post('http://localhost:5001/api/memos', newMemo, {
        headers: { Authorization: `Bearer ${token}` }
      });

      setMemos([...memos, response.data]);
      setEditingId(response.data._id);
      setError('');
    } catch (error) {
      console.error('Error adding memo:', error);
      setError('Failed to add memo. Please try again.');
    }
  };

  const deleteMemo = async (id) => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`http://localhost:5001/api/memos/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMemos(memos.filter(memo => memo._id !== id));
      setError('');
    } catch (error) {
      console.error('Error deleting memo:', error);
      setError('Failed to delete memo. Please try again.');
    }
  };

  const updateMemo = async (id, updates) => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.put(`http://localhost:5001/api/memos/${id}`, updates, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMemos(memos.map(memo => memo._id === id ? response.data : memo));
      setError('');
    } catch (error) {
      console.error('Error updating memo:', error);
      setError('Failed to update memo. Please try again.');
    }
  };

  const handleDragStop = (id, e, d) => {
    updateMemo(id, { x: d.x, y: d.y });
  };

  const handleResizeStop = (id, e, direction, ref, delta, position) => {
    updateMemo(id, {
      width: ref.style.width,
      height: ref.style.height,
      x: position.x,
      y: position.y
    });
  };

  return (
    <div className="relative w-full h-full bg-gray-100 rounded-lg overflow-hidden">
      {error && (
        <div className="absolute top-4 left-1/2 transform -translate-x-1/2 bg-red-100 border border-red-400 text-red-700 px-4 py-2 rounded z-50">
          {error}
        </div>
      )}
      
      {/* Add Note Button */}
      <button
        onClick={addNewMemo}
        className="absolute top-4 right-4 bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded-lg flex items-center gap-2 shadow-lg z-10 transition-all duration-200"
      >
        <FaStickyNote className="text-lg" />
        <span>Add Note</span>
      </button>

      {/* Memo Board */}
      <div 
        ref={boardRef}
        className="w-full h-full p-4"
      >
        {memos.map((memo) => (
          <Rnd
            key={memo._id}
            default={{
              x: memo.x,
              y: memo.y,
              width: memo.width,
              height: memo.height
            }}
            onDragStop={(e, d) => handleDragStop(memo._id, e, d)}
            onResizeStop={(e, direction, ref, delta, position) => 
              handleResizeStop(memo._id, e, direction, ref, delta, position)
            }
            bounds="parent"
            className="shadow-lg"
          >
            <div 
              className="w-full h-full p-4 rounded-lg relative"
              style={{ backgroundColor: memo.color || COLORS[0] }}
            >
              {editingId === memo._id ? (
                <textarea
                  value={memo.content}
                  onChange={(e) => updateMemo(memo._id, { content: e.target.value })}
                  onBlur={() => setEditingId(null)}
                  className="w-full h-full bg-transparent border-none outline-none"
                  autoFocus
                />
              ) : (
                <div 
                  className="w-full h-full cursor-pointer"
                  onClick={() => setEditingId(memo._id)}
                >
                  {memo.content || 'Click to edit...'}
                </div>
              )}
              <button
                onClick={() => deleteMemo(memo._id)}
                className="absolute top-2 right-2 text-gray-600 hover:text-red-500 transition-colors"
              >
                <FaTrash />
              </button>
            </div>
          </Rnd>
        ))}
      </div>
    </div>
  );
};

export default MemoBoard;
