import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, Link } from 'react-router-dom';
import axios from 'axios';
import { FaTrash, FaCheck, FaEdit, FaClock, FaCalendarAlt, FaSignOutAlt, FaTasks, FaCalendar, FaUser, FaCog, FaBell } from 'react-icons/fa';
import Login from './components/Login';
import Signup from './components/Signup';

// Protected Route component
const ProtectedRoute = ({ children }) => {
  const token = localStorage.getItem('token');
  if (!token) {
    return <Navigate to="/login" />;
  }
  return children;
};

// TodoList component
const TodoList = () => {
  const [todos, setTodos] = useState([]);
  const [newTodo, setNewTodo] = useState('');
  const [newDescription, setNewDescription] = useState('');
  const [newDeadline, setNewDeadline] = useState('');
  const [editingTodo, setEditingTodo] = useState(null);
  const [editText, setEditText] = useState('');
  const [editDescription, setEditDescription] = useState('');
  const [editDeadline, setEditDeadline] = useState('');
  const [deleteConfirmId, setDeleteConfirmId] = useState(null);
  const [userProfile, setUserProfile] = useState({
    username: localStorage.getItem('username') || 'User',
    profileImage: localStorage.getItem('profileImage')
  });
  const [currentDateTime, setCurrentDateTime] = useState(new Date());
  const [activeTab, setActiveTab] = useState('tasks'); // 'tasks' or 'calendar'
  const [showProfileMenu, setShowProfileMenu] = useState(false);

  useEffect(() => {
    fetchTodos();
    // Update time every second
    const timer = setInterval(() => {
      setCurrentDateTime(new Date());
    }, 1000);

    return () => clearInterval(timer);
  }, []);

  const formatCurrentDateTime = (date) => {
    return date.toLocaleString('en-US', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
      second: '2-digit',
      hour12: true
    });
  };

  const fetchTodos = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:5001/api/todos', {
        headers: { Authorization: `Bearer ${token}` }
      });
      console.log('Fetched todos:', response.data);
      setTodos(response.data);
    } catch (error) {
      console.error('Error fetching todos:', error);
      if (error.response?.status === 401) {
        localStorage.removeItem('token');
        window.location.href = '/login';
      }
    }
  };

  const addTodo = async (e) => {
    e.preventDefault();
    if (!newTodo.trim()) return;

    const todoData = {
      text: newTodo.trim(),
      description: newDescription.trim(),
      deadline: newDeadline || null,
      completed: false
    };

    console.log('Sending todo data:', todoData);

    try {
      const token = localStorage.getItem('token');
      const response = await axios.post('http://localhost:5001/api/todos', todoData, {
        headers: { Authorization: `Bearer ${token}` }
      });
      console.log('Server response:', response.data);
      
      const newTodoItem = response.data;
      setTodos(prevTodos => [newTodoItem, ...prevTodos]);
      
      setNewTodo('');
      setNewDescription('');
      setNewDeadline('');
    } catch (error) {
      console.error('Error adding todo:', error);
    }
  };

  const toggleTodo = async (id) => {
    try {
      const token = localStorage.getItem('token');
      const todo = todos.find(t => t._id === id);
      const response = await axios.put(`http://localhost:5001/api/todos/${id}`, {
        ...todo,
        completed: !todo.completed
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setTodos(todos.map(todo => 
        todo._id === id ? response.data : todo
      ));
    } catch (error) {
      console.error('Error toggling todo:', error);
    }
  };

  const confirmDelete = (id) => {
    setDeleteConfirmId(id);
  };

  const cancelDelete = () => {
    setDeleteConfirmId(null);
  };

  const deleteTodo = async (id) => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`http://localhost:5001/api/todos/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setTodos(todos.filter(todo => todo._id !== id));
      setDeleteConfirmId(null);
    } catch (error) {
      console.error('Error deleting todo:', error);
    }
  };

  const startEditing = (todo) => {
    console.log('Starting edit for todo:', todo);
    setEditingTodo(todo._id);
    setEditText(todo.text);
    setEditDescription(todo.description || '');
    setEditDeadline(todo.deadline ? new Date(todo.deadline).toISOString().split('T')[0] : '');
  };

  const saveEdit = async (id) => {
    const editData = {
      text: editText.trim(),
      description: editDescription.trim(),
      deadline: editDeadline || null
    };

    console.log('Saving edit with data:', editData);

    try {
      const token = localStorage.getItem('token');
      const response = await axios.put(`http://localhost:5001/api/todos/${id}`, editData, {
        headers: { Authorization: `Bearer ${token}` }
      });
      console.log('Edit response:', response.data);
      setTodos(todos.map(todo => 
        todo._id === id ? response.data : todo
      ));
      setEditingTodo(null);
    } catch (error) {
      console.error('Error updating todo:', error);
    }
  };

  const formatDeadline = (deadline) => {
    if (!deadline) return null;
    const date = new Date(deadline);
    return date.toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  const isOverdue = (deadline) => {
    if (!deadline) return false;
    return new Date(deadline) < new Date();
  };

  const formatDateTime = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('userId');
    window.location.href = '/login';
  };

  const handleProfileClick = () => {
    setShowProfileMenu(!showProfileMenu);
  };

  const handleClickOutside = (event) => {
    if (!event.target.closest('.profile-menu')) {
      setShowProfileMenu(false);
    }
  };

  useEffect(() => {
    document.addEventListener('click', handleClickOutside);
    return () => {
      document.removeEventListener('click', handleClickOutside);
    };
  }, []);

  // Helper to group todos by date
  const groupTodosByDate = (todos) => {
    const groups = {};
    todos.forEach(todo => {
      const dateKey = todo.deadline ? new Date(todo.deadline).toLocaleDateString('en-US', {
        year: 'numeric', month: 'long', day: 'numeric'
      }) : 'No Date';
      if (!groups[dateKey]) groups[dateKey] = [];
      groups[dateKey].push(todo);
    });
    return groups;
  };

  return (
    <div className="min-h-screen bg-gray-100 font-cal">
      {/* Navigation Panel with Profile */}
      <div className="bg-white shadow-md">
        <div className="max-w-2xl mx-auto px-4">
          <div className="flex items-center justify-between py-4">
            {/* Profile Section with Logo */}
            <div className="relative profile-menu flex items-center gap-3">
              <img src="/logo.png" alt="DotDo Logo" className="w-10 h-10 rounded-lg shadow" />
              <button
                onClick={handleProfileClick}
                className="flex items-center gap-3 hover:bg-gray-100 rounded-lg px-2 py-1 transition-colors"
              >
                {userProfile.profileImage ? (
                  <img
                    src={`http://localhost:5001/${userProfile.profileImage}`}
                    alt="Profile"
                    className="w-10 h-10 rounded-full object-cover border-2 border-blue-500"
                  />
                ) : (
                  <div className="w-10 h-10 rounded-full bg-blue-500 flex items-center justify-center text-white text-lg font-bold">
                    {userProfile.username.charAt(0).toUpperCase()}
                  </div>
                )}
                <span className="text-lg font-bold text-gray-800">{userProfile.username}</span>
                <svg
                  className={`w-4 h-4 text-gray-500 transition-transform ${showProfileMenu ? 'rotate-180' : ''}`}
                  fill="none"
                  stroke="currentColor"
                  viewBox="0 0 24 24"
                >
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                </svg>
              </button>

              {/* Profile Dropdown Menu */}
              {showProfileMenu && (
                <div className="absolute left-0 mt-2 w-48 bg-white rounded-lg shadow-lg py-2 z-10">
                  <button className="w-full px-4 py-2 text-left text-gray-700 hover:bg-gray-100 flex items-center gap-2">
                    <FaUser className="text-gray-500" />
                    My Profile
                  </button>
                  <button className="w-full px-4 py-2 text-left text-gray-700 hover:bg-gray-100 flex items-center gap-2">
                    <FaCog className="text-gray-500" />
                    Settings
                  </button>
                  <button className="w-full px-4 py-2 text-left text-gray-700 hover:bg-gray-100 flex items-center gap-2">
                    <FaBell className="text-gray-500" />
                    Notifications
                  </button>
                  <div className="border-t border-gray-200 my-1"></div>
                  <button 
                    onClick={handleLogout}
                    className="w-full px-4 py-2 text-left text-red-600 hover:bg-gray-100 flex items-center gap-2"
                  >
                    <FaSignOutAlt className="text-red-500" />
                    Logout
                  </button>
                </div>
              )}
            </div>

            {/* Navigation Tabs */}
            <div className="flex items-center space-x-8">
              <button
                onClick={() => setActiveTab('tasks')}
                className={`px-6 py-2 rounded-lg font-medium transition-colors flex items-center gap-2 ${
                  activeTab === 'tasks'
                    ? 'bg-blue-500 text-white'
                    : 'text-gray-600 hover:bg-gray-100'
                }`}
              >
                <FaTasks className="text-lg" />
                My Tasks
              </button>
              <button
                onClick={() => setActiveTab('calendar')}
                className={`px-6 py-2 rounded-lg font-medium transition-colors flex items-center gap-2 ${
                  activeTab === 'calendar'
                    ? 'bg-blue-500 text-white'
                    : 'text-gray-600 hover:bg-gray-100'
                }`}
              >
                <FaCalendar className="text-lg" />
                Calendar
              </button>
            </div>
          </div>
        </div>
      </div>

      <div className="py-8">
        <div className="max-w-2xl mx-auto px-4">
          <div className="flex flex-col items-center mb-8">
            <div className="text-lg text-gray-600 font-medium">
              {formatCurrentDateTime(currentDateTime)}
            </div>
            <h1 className="text-6xl font-bold text-gray-800 cal-sans-text mt-2">
              DotDo
            </h1>
            <div className="text-base text-gray-500 font-light mt-1 tracking-wide">
              DotDo — Stay on Point.
            </div>
          </div>

          {activeTab === 'tasks' ? (
            <>
              <form onSubmit={addTodo} className="mb-8 space-y-4 font-cal">
                <div>
                  <input
                    type="text"
                    value={newTodo}
                    onChange={(e) => setNewTodo(e.target.value)}
                    placeholder="Add a new todo..."
                    className="w-full px-4 py-2 rounded-lg border border-gray-300 focus:outline-none focus:border-blue-500 font-cal"
                  />
                </div>
                <div>
                  <textarea
                    value={newDescription}
                    onChange={(e) => setNewDescription(e.target.value)}
                    placeholder="Add a description (optional)..."
                    className="w-full px-4 py-2 rounded-lg border border-gray-300 focus:outline-none focus:border-blue-500 font-cal"
                    rows="2"
                  />
                </div>
                <div>
                  <input
                    type="date"
                    value={newDeadline}
                    onChange={(e) => setNewDeadline(e.target.value)}
                    className="w-full px-4 py-2 rounded-lg border border-gray-300 focus:outline-none focus:border-blue-500 font-cal"
                  />
                </div>
                <button
                  type="submit"
                  className="w-full px-6 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-colors font-cal"
                >
                  Add Todo
                </button>
              </form>

              <div className="space-y-4 font-cal">
                {todos.map((todo) => (
                  <div
                    key={todo._id}
                    className="bg-white rounded-lg shadow p-4 font-cal"
                  >
                    {editingTodo === todo._id ? (
                      <div className="space-y-4 font-cal">
                        <input
                          type="text"
                          value={editText}
                          onChange={(e) => setEditText(e.target.value)}
                          className="w-full px-4 py-2 rounded-lg border border-gray-300 focus:outline-none focus:border-blue-500 font-cal"
                        />
                        <textarea
                          value={editDescription}
                          onChange={(e) => setEditDescription(e.target.value)}
                          placeholder="Add a description (optional)..."
                          className="w-full px-4 py-2 rounded-lg border border-gray-300 focus:outline-none focus:border-blue-500 font-cal"
                          rows="2"
                        />
                        <input
                          type="date"
                          value={editDeadline}
                          onChange={(e) => setEditDeadline(e.target.value)}
                          className="w-full px-4 py-2 rounded-lg border border-gray-300 focus:outline-none focus:border-blue-500 font-cal"
                        />
                        <div className="flex justify-end space-x-2">
                          <button
                            onClick={() => saveEdit(todo._id)}
                            className="px-4 py-2 bg-green-500 text-white rounded-lg hover:bg-green-600 transition-colors font-cal"
                          >
                            Save
                          </button>
                          <button
                            onClick={() => setEditingTodo(null)}
                            className="px-4 py-2 bg-gray-500 text-white rounded-lg hover:bg-gray-600 transition-colors font-cal"
                          >
                            Cancel
                          </button>
                        </div>
                      </div>
                    ) : (
                      <div className="space-y-2 font-cal">
                        <div className="flex items-center justify-between">
                          <div className="flex items-center gap-3">
                            <button
                              onClick={() => toggleTodo(todo._id)}
                              className={`w-6 h-6 rounded-full border-2 flex items-center justify-center font-cal
                                ${todo.completed 
                                  ? 'bg-green-500 border-green-500 text-white' 
                                  : 'border-gray-300 hover:border-green-500'
                                }`}
                            >
                              {todo.completed && <FaCheck className="w-3 h-3" />}
                            </button>
                            <span className={`text-lg font-cal ${todo.completed ? 'line-through text-gray-500' : 'text-gray-800'}`}>
                              {todo.text}
                            </span>
                          </div>
                          <div className="flex items-center gap-2">
                            <button
                              onClick={() => startEditing(todo)}
                              className="p-2 text-gray-500 hover:text-blue-500 transition-colors"
                            >
                              <FaEdit />
                            </button>
                            {deleteConfirmId === todo._id ? (
                              <div className="flex items-center gap-2">
                                <button
                                  onClick={() => deleteTodo(todo._id)}
                                  className="p-2 text-red-500 hover:text-red-600 transition-colors"
                                >
                                  <FaTrash />
                                </button>
                                <button
                                  onClick={cancelDelete}
                                  className="text-sm text-gray-500 hover:text-gray-600"
                                >
                                  Cancel
                                </button>
                              </div>
                            ) : (
                              <button
                                onClick={() => confirmDelete(todo._id)}
                                className="p-2 text-gray-500 hover:text-red-500 transition-colors"
                              >
                                <FaTrash />
                              </button>
                            )}
                          </div>
                        </div>
                        {todo.description && (
                          <p className="text-gray-600 ml-9">{todo.description}</p>
                        )}
                        {todo.deadline && (
                          <div className="flex items-center gap-2 ml-9 text-sm">
                            <FaCalendarAlt className="text-gray-500" />
                            <span className={`${isOverdue(todo.deadline) && !todo.completed ? 'text-red-500' : 'text-gray-500'}`}>
                              {formatDeadline(todo.deadline)}
                              {isOverdue(todo.deadline) && !todo.completed && ' (Overdue)'}
                            </span>
                          </div>
                        )}
                        <div className="flex items-center gap-2 ml-9 text-sm text-gray-500">
                          <FaClock />
                          <span>Created: {formatDateTime(todo.createdAt)}</span>
                        </div>
                      </div>
                    )}
                  </div>
                ))}
              </div>
            </>
          ) : (
            <div className="bg-white rounded-lg shadow p-6">
              <h2 className="text-2xl font-bold text-gray-800 mb-4">Calendar View</h2>
              {/* Calendar grouped list */}
              {Object.entries(groupTodosByDate(todos)).length === 0 ? (
                <p className="text-gray-600">No tasks to display.</p>
              ) : (
                <div className="space-y-6">
                  {Object.entries(groupTodosByDate(todos)).map(([date, tasks]) => (
                    <div key={date}>
                      <div className="text-lg font-semibold text-blue-700 mb-2">{date}</div>
                      <ul className="space-y-2">
                        {tasks.map(task => (
                          <li key={task._id} className="flex items-center gap-2 p-2 bg-gray-50 rounded">
                            <span className={`w-3 h-3 rounded-full ${task.completed ? 'bg-green-500' : 'bg-gray-300'} inline-block`}></span>
                            <span className={`font-cal ${task.completed ? 'line-through text-gray-400' : 'text-gray-800'}`}>{task.text}</span>
                            {task.description && <span className="text-gray-500 text-sm ml-2">({task.description})</span>}
                          </li>
                        ))}
                      </ul>
                    </div>
                  ))}
                </div>
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

// Main App component
function App() {
  return (
    <Router>
      <Routes>
        <Route path="/login" element={<Login />} />
        <Route path="/signup" element={<Signup />} />
        <Route
          path="/"
          element={
            <ProtectedRoute>
              <TodoList />
            </ProtectedRoute>
          }
        />
      </Routes>
    </Router>
  );
}

export default App;
