import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { Rnd } from 'react-rnd';
import { FaTrash, FaEdit, FaSave, FaTimes } from 'react-icons/fa';

const DEFAULT_WIDTH = 200;
const DEFAULT_HEIGHT = 200;
const DEFAULT_COLOR = '#fffde4'; // light yellow

const MemoBoard = () => {
  const [memos, setMemos] = useState([]);
  const [editingMemo, setEditingMemo] = useState(null);
  const [editText, setEditText] = useState('');
  const boardRef = useRef(null);

  useEffect(() => {
    fetchMemos();
  }, []);

  const fetchMemos = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:5001/api/memos', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMemos(response.data);
    } catch (error) {
      console.error('Error fetching memos:', error);
    }
  };

  const addMemo = async (e) => {
    const rect = boardRef.current.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post('http://localhost:5001/api/memos', {
        content: '',
        x,
        y,
        width: DEFAULT_WIDTH,
        height: DEFAULT_HEIGHT
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMemos([...memos, response.data]);
      setEditingMemo(response.data._id);
      setEditText('');
    } catch (error) {
      console.error('Error adding memo:', error);
    }
  };

  const deleteMemo = async (id) => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`http://localhost:5001/api/memos/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMemos(memos.filter(memo => memo._id !== id));
    } catch (error) {
      console.error('Error deleting memo:', error);
    }
  };

  const startEditing = (memo) => {
    setEditingMemo(memo._id);
    setEditText(memo.content);
  };

  const saveEdit = async (id) => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.put(`http://localhost:5001/api/memos/${id}`, {
        content: editText
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMemos(memos.map(memo => 
        memo._id === id ? response.data : memo
      ));
      setEditingMemo(null);
    } catch (error) {
      console.error('Error updating memo:', error);
    }
  };

  const onDragStop = async (id, d) => {
    try {
      const token = localStorage.getItem('token');
      await axios.put(`http://localhost:5001/api/memos/${id}`, {
        x: d.x,
        y: d.y
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMemos(memos.map(memo => memo._id === id ? { ...memo, x: d.x, y: d.y } : memo));
    } catch (error) {
      console.error('Error moving memo:', error);
    }
  };

  const onResizeStop = async (id, direction, ref, delta, position) => {
    try {
      const token = localStorage.getItem('token');
      await axios.put(`http://localhost:5001/api/memos/${id}`, {
        width: parseInt(ref.style.width),
        height: parseInt(ref.style.height),
        x: position.x,
        y: position.y
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMemos(memos.map(memo => memo._id === id ? { ...memo, width: parseInt(ref.style.width), height: parseInt(ref.style.height), x: position.x, y: position.y } : memo));
    } catch (error) {
      console.error('Error resizing memo:', error);
    }
  };

  return (
    <div className="w-full h-full min-h-[calc(100vh-4rem)] flex items-center justify-center overflow-auto bg-gray-800 p-4">
      <div
        ref={boardRef}
        className="w-full max-w-7xl h-[calc(100vh-8rem)] bg-gray-700 relative rounded-xl shadow-2xl border-2 border-gray-900 cursor-pointer"
        onClick={e => {
          if (e.target === boardRef.current) addMemo(e);
        }}
      >
        {memos.map((memo) => (
          <Rnd
            key={memo._id}
            size={{ width: memo.width || DEFAULT_WIDTH, height: memo.height || DEFAULT_HEIGHT }}
            position={{ x: memo.x || 100, y: memo.y || 100 }}
            minWidth={120}
            minHeight={100}
            bounds="parent"
            onDragStop={(e, d) => onDragStop(memo._id, d)}
            onResizeStop={(e, direction, ref, delta, position) => onResizeStop(memo._id, direction, ref, delta, position)}
            style={{ zIndex: editingMemo === memo._id ? 10 : 1 }}
          >
            <div
              className="w-full h-full rounded-lg shadow-md p-3 relative flex flex-col"
              style={{
                background: DEFAULT_COLOR,
                boxShadow: '2px 4px 12px rgba(0,0,0,0.12)',
                border: '1.5px solid #f7e98d',
                cursor: editingMemo === memo._id ? 'text' : 'move',
                userSelect: 'none',
              }}
              onClick={e => { e.stopPropagation(); }}
            >
              {editingMemo === memo._id ? (
                <>
                  <textarea
                    value={editText}
                    onChange={e => setEditText(e.target.value)}
                    className="w-full h-full bg-transparent border-none outline-none resize-none text-base font-medium"
                    autoFocus
                    style={{ background: 'transparent' }}
                    rows={4}
                  />
                  <div className="flex justify-end gap-2 mt-2">
                    <button
                      onClick={() => saveEdit(memo._id)}
                      className="p-1 text-green-600 hover:text-green-700"
                    >
                      <FaSave />
                    </button>
                    <button
                      onClick={() => setEditingMemo(null)}
                      className="p-1 text-gray-600 hover:text-gray-700"
                    >
                      <FaTimes />
                    </button>
                  </div>
                </>
              ) : (
                <>
                  <div
                    className="flex-1 whitespace-pre-wrap text-base font-medium"
                    style={{ wordBreak: 'break-word', cursor: 'pointer' }}
                    onDoubleClick={() => startEditing(memo)}
                  >
                    {memo.content || <span className="text-gray-400">(Double-click to edit)</span>}
                  </div>
                  <div className="absolute top-2 right-2 flex gap-2 opacity-80">
                    <button
                      onClick={() => startEditing(memo)}
                      className="p-1 text-blue-600 hover:text-blue-700"
                    >
                      <FaEdit />
                    </button>
                    <button
                      onClick={() => deleteMemo(memo._id)}
                      className="p-1 text-red-600 hover:text-red-700"
                    >
                      <FaTrash />
                    </button>
                  </div>
                </>
              )}
            </div>
          </Rnd>
        ))}
      </div>
    </div>
  );
};

export default MemoBoard;
