import React, { useState, useEffect } from 'react';
import { FaPlus, FaTrash, FaEdit, FaCheck } from 'react-icons/fa';
import axios from 'axios';

const MemoBoard = () => {
  const [memos, setMemos] = useState([]);
  const [newMemo, setNewMemo] = useState('');
  const [editingMemo, setEditingMemo] = useState(null);
  const [editText, setEditText] = useState('');
  const [isExpanded, setIsExpanded] = useState(false);

  useEffect(() => {
    fetchMemos();
  }, []);

  const fetchMemos = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:5001/api/memos', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMemos(response.data);
    } catch (error) {
      console.error('Error fetching memos:', error);
    }
  };

  const addMemo = async (e) => {
    e.preventDefault();
    if (!newMemo.trim()) return;

    try {
      const token = localStorage.getItem('token');
      const response = await axios.post('http://localhost:5001/api/memos', 
        { text: newMemo.trim() },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setMemos([...memos, response.data]);
      setNewMemo('');
    } catch (error) {
      console.error('Error adding memo:', error);
    }
  };

  const deleteMemo = async (id) => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`http://localhost:5001/api/memos/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMemos(memos.filter(memo => memo._id !== id));
    } catch (error) {
      console.error('Error deleting memo:', error);
    }
  };

  const startEditing = (memo) => {
    setEditingMemo(memo._id);
    setEditText(memo.text);
  };

  const saveEdit = async (id) => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.put(`http://localhost:5001/api/memos/${id}`,
        { text: editText.trim() },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setMemos(memos.map(memo => 
        memo._id === id ? response.data : memo
      ));
      setEditingMemo(null);
    } catch (error) {
      console.error('Error updating memo:', error);
    }
  };

  return (
    <div className="bg-white rounded-lg shadow-md p-4 mb-4">
      <div className="flex items-center justify-between mb-4">
        <h3 className="text-lg font-semibold text-gray-800">Quick Memos</h3>
        <button
          onClick={() => setIsExpanded(!isExpanded)}
          className="text-gray-500 hover:text-gray-700"
        >
          {isExpanded ? '▼' : '▶'}
        </button>
      </div>

      {isExpanded && (
        <>
          <form onSubmit={addMemo} className="mb-4">
            <div className="flex gap-2">
              <input
                type="text"
                value={newMemo}
                onChange={(e) => setNewMemo(e.target.value)}
                placeholder="Add a quick memo..."
                className="flex-1 px-3 py-2 border rounded-lg focus:outline-none focus:border-blue-500"
              />
              <button
                type="submit"
                className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 flex items-center gap-2"
              >
                <FaPlus />
                Add
              </button>
            </div>
          </form>

          <div className="space-y-2 max-h-48 overflow-y-auto">
            {memos.map((memo) => (
              <div
                key={memo._id}
                className="flex items-center justify-between p-2 bg-gray-50 rounded-lg"
              >
                {editingMemo === memo._id ? (
                  <div className="flex-1 flex gap-2">
                    <input
                      type="text"
                      value={editText}
                      onChange={(e) => setEditText(e.target.value)}
                      className="flex-1 px-2 py-1 border rounded"
                    />
                    <button
                      onClick={() => saveEdit(memo._id)}
                      className="p-1 text-green-500 hover:text-green-600"
                    >
                      <FaCheck />
                    </button>
                  </div>
                ) : (
                  <>
                    <span className="flex-1 text-gray-700">{memo.text}</span>
                    <div className="flex gap-2">
                      <button
                        onClick={() => startEditing(memo)}
                        className="p-1 text-blue-500 hover:text-blue-600"
                      >
                        <FaEdit />
                      </button>
                      <button
                        onClick={() => deleteMemo(memo._id)}
                        className="p-1 text-red-500 hover:text-red-600"
                      >
                        <FaTrash />
                      </button>
                    </div>
                  </>
                )}
              </div>
            ))}
          </div>
        </>
      )}
    </div>
  );
};

export default MemoBoard;
